mapper.select:
select a.invest_symbol, a.invest_date, a.invest_price
from invest a join user_invest b on a.invest_id = b.invest_id
where b.user_id = #{userId}
order by 3 desc;

select a.realestate_name, a.realestate_price
from realestate a join user_realestate b on a.realestate_id = b.realestate_id
where b.user_id = #{userId}
order by 2 desc;

select a.car_type, a.car_amount
from car a join user_car b on a.car_id = b.car_id
where b.user_id = #{userId}
order by 2 desc;

select a.account_name, a.account_number, a.balance, a.account_type
from account a join user_account b on a.account_id = b.account_id
where b.user_id = #{userId}
order by 2 desc;
