mapper.select:
select a.board_num, a.user_name, b.nick_name, a.title, a.content, a.created_at
from board a join user b on a.user_name = b.user_name
order by created_at desc;

select a.board_num, a.user_name, b.nick_name, a.title, a.content, a.created_at
from board a join user b on a.user_name = b.user_name
where a.board_num = #{boardNum}
order by 6 desc;

mapper.update:
update board
set del = 1
where board_num = #{boardNum};

update board
set content = #{content}
where board_num = #{boardNum};

mapper.select:
select reply_num, reply, reply_at
from board_reply
where board_num = #{boardNum}
order by 2 desc;
