mapper.select:
select user_id, user_name, nick_name, email, gender, age, role
from user
where user_name=#{userName}

mapper.update:
UPDATE user
SET age=#{request.age}, gender=#{request.gender}
WHERE user_name=#{user_name}

UPDATE user
SET refresh_token=#{refreshToken}
WHERE user_name=#{userName}

